package com.pe.demneru.client.jms.main;import javax.jms.JMSException;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import com.pe.demneru.client.jms.UtilJMS;import com.pe.demneru.client.jms.provider.JmsProvider;public class Main{    private static final Logger logger = LogManager.getLogger( Main.class );    public static void main( String[] args ){    logger.trace( "Entering application." );    JmsProvider provider = new JmsProvider();    provider.setJndiQueue( "pe.com.demneru.jms.queue" );    provider.setJndiQueueFactory( "pe.com.demneru.jms.connectionfactory" );    provider.setProvider( "t3://localhost:7001" );    UtilJMS clientJms = new UtilJMS( provider );    System.out.println( clientJms.toString() );    try{      clientJms.enviarMensajeJMSPuntoAPunto( "Mensaje de prueba 2" );    }    catch( JMSException e ){      logger.error( e);    }    System.out.println( "se ha terminado de enviar el mensaje" );  }}